sysauto_script.script:
// get Today's date and time
rightNow = new GlideDateTime();
gs.info(rightNow);

// Query the database for NeedIt Task records with Due date field values within
// 24 hours from now.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.

var tasksDueSoon = new GlideRecord('x_58872_needit_needit_task');
tasksDueSoon.addQuery('due_date','>=',rightNow);
tasksDueSoon.addQuery('due_date','<',gs.hoursAgo(-24));
tasksDueSoon.addQuery('state','!=',3);
tasksDueSoon.query();

while(tasksDueSoon.next()){
	// Write a log message for each NeedIt Task Record that is due soon
	gs.info("NeedIt Task due soon record = " + tasksDueSoon.number);
	gs.eventQueue('x_58872_needit.soonDueNeedItTask',tasksDueSoon,tasksDueSoon.number,gs.getUserName());
}
